import React, { useState } from 'react'
import './expenseform.css'

const Expenseform = (props) => {
        const [enteredTitle, Settitle] = useState('')
        const [enteredAmount, SetAmount] = useState(0.01);
        const [enteredDate, SetDate] = useState('');
        const typehandler = (event) => {
                Settitle(event.target.value);

        }
        const SetAmountt = (event) => {
                SetAmount(event.target.value);

        }
        const SetDatee = (event) => {
                SetDate(event.target.value);

        }
        const submitHandler = (event) => {
                event.preventDefault();
                const ExpenseData = {
                        title: enteredTitle,
                        amount: enteredAmount,
                        date: new Date(enteredDate)

                }
                props.saveexpense(ExpenseData);
                Settitle('');
                SetAmount('');
                SetDate('');
        }

        return (
                <form onSubmit={submitHandler}>
                        <div className='new-expense__controls'>
                                <div className='new-expense__control'>
                                        <label>Title</label>
                                        <input type="text" onChange={typehandler} value={enteredTitle} />
                                </div>
                                <div className='new-expense__control'>
                                        <label>Amount</label>
                                        <input type="number" min='0.01' step='0.01' onChange={SetAmountt} value={enteredAmount} />
                                </div>
                                <div className='new-expense__control'>
                                        <label>Date</label>
                                        <input type="date" onChange={SetDatee} value={enteredDate} />
                                </div>
                        </div>
                        <div className='new-expense__actions'>
                                <button type="submit">Add Expense</button>
                        </div>
                </form>
        )
}

export default Expenseform
